mapper.insert:
insert into Posts
(
	postNo
	,memberId
	,postTitle
	,postContent
	,postDate
	,isAnnouncement
	,postType

		,originalFile
		,savedFile

)
values
(
	postNoSeq.nextval
	,#{memberId}
	,#{postTitle}
	,#{postContent}
	,sysdate
	,#{isAnnouncement}
	,#{postType}

		,#{originalFile}
		,#{savedFile}

)
